# --- Upload CSV ---
uploaded_file = st.file_uploader("üìÅ Upload your SNP CSV file", type="csv")

if uploaded_file:
    file_text = uploaded_file.read().decode("utf-8")
    st.success("‚úÖ File uploaded successfully!")
    csv_reader = csv.DictReader(io.StringIO(file_text))
    data = list(csv_reader)

    st.write("### üëÅÔ∏è Preview of Uploaded Data")
    st.dataframe(data)

    # --- SNP ID Search ---
    user_input = st.text_input("üîç Enter SNP ID to search (e.g., rs12345)")

    if user_input:
        matches = [row for row in data if row['SNP ID'].lower() == user_input.lower()]
        if matches:
            st.success("‚úÖ SNP found in your file:")
            st.write(matches[0])

            snp_id_clean = user_input.lower()
            if snp_id_clean in snp_annotations:
                annotation = snp_annotations[snp_id_clean]
                st.markdown("### üìå Annotation from NutraDatabase")
                st.write(f"**Gene:** {annotation['gene']}")
                st.write(f"**Nutrient Affected:** {annotation['nutrient']}")
                st.write(f"**Impact:** {annotation['impact']}")
                st.write(f"**Recommendation:** {annotation['recommendation']}")
                st.markdown(f"[üìñ PubMed Reference]({annotation['reference']})")
            else:
                st.info("‚ÑπÔ∏è No annotation found in database for this SNP.")
        else:
            st.warning("‚ö†Ô∏è SNP not found. Please try again.")

    # --- Show All Annotated SNPs ---
    st.markdown("---")
    if st.button("üîé Show All Annotated SNPs in My File"):
        annotated_matches = []
        for row in data:
            snp_id = row['SNP ID'].lower()
            if snp_id in snp_annotations:
                row_with_annotation = row.copy()
                row_with_annotation.update(snp_annotations[snp_id])
                annotated_matches.append(row_with_annotation)

        if annotated_matches:
            st.write(f"### üßæ Found {len(annotated_matches)} annotated SNPs in your file:")
            st.dataframe(annotated_matches)
        else:
            st.warning("üòï No SNPs from your file matched our annotation database.")
else:
    st.info("üëÜ Please upload a CSV file to begin.")
